import React from "react";
import { useState, useEffect, useRef } from "react";
import { ReactComponent as DownArrow } from "../assets/down-arrow.svg";
import { ReactComponent as UpArrow } from "../assets/up-arrow.svg";
import Action from "./Action";

const Comment = ({ comments }) => {
  const [input, setInput] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [showInput, setShowInput] = useState(false);
  const [expand, setExpand] = useState(false);


  const onAddComment = () => {};

  const handleNewComment = () => {
    setExpand(!expand);
    setShowInput(true);
  };

  return (
    <div>
      <div
        className={comments.id === 1 ? "inputContainer" : "commentContainer"}
      >
        {comments.id === 1 ? (
          <>
            <input
              type="text"
              className="inputContainer__input first_input"
              autoFocus
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="type...."
            />
            <Action
              className="reply comment"
              type="COMMENT"
              handleClick={onAddComment}
            />
          </>
        ) : (
          <>
            <span style={{ wordWrap: "break-word" }}>{comments.name}</span>
            <div style={{ display: "flex", marginTop: "5px" }}>
              {editMode ? (
                <>
                  <Action className="reply" type="SAVE" />
                  <Action
                    className="reply"
                    type="CANCEL"
                    handleClick={() => {
                      setEditMode(false);
                    }}
                  />
                </>
              ) : (
                <>
                  <Action className="reply" type="LIKE" />
                  <Action
                    className="reply"
                    type={
                        <>

                        </>
                    }
                    handleClick={handleNewComment}
                  />
                  <Action
                    className="reply"
                    type="EDIT"
                    handleClick={() => {
                      setEditMode(true);
                    }}
                  />
                  <Action className="reply" type="DELETE" />
                </>
              )}
            </div>
          </>
        )}
      </div>

      <div style={{ paddingLeft: 25 }}>
        {showInput && (
          <div className="inputContainer">
            <input
              type="text"
              className="inputContainer__input"
              autoFocus
              onChange={(e) => setInput(e.target.value)}
            />
            <Action
              className="reply"
              type="REPLY"
            //   handleClick={handleNewComment}
            />
            <Action
              className="reply"
              type="CANCEL"
              handleClick={() => {
                setShowInput(false);
                // if (!comments?.items?.length) setExpand(false);
              }}
            />
          </div>
        )}
        {comments?.items?.map((comment) => {
          return <Comment key={comment.id} comments={comment} />;
        })}
      </div>
    </div>
  );
};

export default Comment;
