import React from "react";
import { useState, useEffect, useRef } from "react";
import { ReactComponent as DownArrow } from "../assets/down-arrow.svg";
import { ReactComponent as UpArrow } from "../assets/up-arrow.svg";
import Profile from "../assets/avatar.jpg";
import Like from "../assets/like.png";
import Action from "./Action";

const Comment = ({
  comments,
  handleInsertNode,
  handleEditNode,
  handleDeleteNode,
}) => {
  const [input, setInput] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [showInput, setShowInput] = useState(false);
  const [expand, setExpand] = useState(false);
  const inputRef = useRef(null);

  const onAddComment = () => {
    if (editMode) {
      handleEditNode(comments.id, inputRef?.current?.innerText);
    } else {
      setExpand(true);
      handleInsertNode(comments.id, input);
      setInput("");
      setShowInput(false);
    }

    if (editMode) setEditMode(false);
  };

  const handleNewComment = () => {
    setExpand(!expand);
    setShowInput(true);
  };

  const handleDelete = () => {
    handleDeleteNode(comments.id);
  };

  useEffect(() => {
    inputRef?.current?.focus();
  }, [editMode]);

  return (
    <div>
      <div
        className={comments.id === 1 ? "inputContainer" : "commentContainer"}
      >
        {comments.id === 1 ? (
          <>
          <img src={Profile} alt="profile"/>
            <input
              type="text"
              className="inputContainer__input first_input"
              autoFocus
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="What are your thoughts ?"
            />
            <Action
              className="reply comment"
              type="COMMENT"
              handleClick={onAddComment}
            />
          </>
        ) : (
          <>
            <span
              contentEditable={editMode}
              suppressContentEditableWarning={editMode}
              ref={inputRef}
              style={{ wordWrap: "break-word" }}
            >
              {comments.name}
            </span>
            <div style={{ display: "flex", marginTop: "5px" }}>
              {editMode ? (
                <>
                  <Action
                    className="reply"
                    type="SAVE"
                    handleClick={onAddComment}
                  />
                  <Action
                    className="reply"
                    type="CANCEL"
                    handleClick={() => {
                      if (inputRef.current)
                        inputRef.current.innerText = comments.name;
                      setEditMode(false);
                    }}
                  />
                </>
              ) : (
                <>
                  {/* <Action className="reply" type="LIKE" /> */}
                  <img src={Like} alt="profile"/>
                  <Action
                    className="reply"
                    type={
                      <>
                        {expand ? (
                          <UpArrow width="10px" height="10px" />
                        ) : (
                          <DownArrow width="10px" height="10px" />
                        )}{" "}
                        REPLY
                      </>
                    }
                    handleClick={handleNewComment}
                  />
                  <Action
                    className="reply"
                    type="EDIT"
                    handleClick={() => {
                      setEditMode(true);
                    }}
                  />
                  <Action
                    className="reply"
                    type="DELETE"
                    handleClick={handleDelete}
                  />
                </>
              )}
            </div>
          </>
        )}
      </div>

      <div style={{ display: expand ? "block" : "none", paddingLeft: 25 }}>
        {showInput && (
          <div className="inputContainer">
            <input
              type="text"
              className="inputContainer__input"
              autoFocus
              onChange={(e) => setInput(e.target.value)}
            />
            <Action className="reply" type="REPLY" handleClick={onAddComment} />
            <Action
              className="reply"
              type="CANCEL"
              handleClick={() => {
                setShowInput(false);
                if (!comments?.items?.length) setExpand(false);
              }}
            />
          </div>
        )}
        {comments?.items?.map((comment) => {
          return (
            <Comment
              key={comment.id}
              comments={comment}
              handleInsertNode={handleInsertNode}
              handleEditNode={handleEditNode}
              handleDeleteNode={handleDeleteNode}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Comment;
